package java_exam;

// Class => 프로그램의 최소 실행 단위 
public class HelloWorld {

//	메소드: 프로그램이 어떤 방법으로 실행이 될 것인지 작성하는 영역
//	public static void main(String[] args) {...}
//	= HelloWorld 클래스를 실행시킬 수 있는 메소드
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//"Hello, World!" 문장 출력해보기
		// 출력을 위한 코드 : System.out.println( 출력 문장 혹은 수식 );
		System.out.println("Hello, World!");
		// 글자는 "" 필수
		System.out.println(30303030);
		System.out.println(30303030.32121);
		
		System.out.println(3 + 3);
		System.out.println(3 - 2);
		System.out.println(3 / 3);  // div 연산
		System.out.println(3 % 2);	// % = mod 연산자	
		System.out.println(3 * 9); // * = 아스타
		
//		실행 방법(Windows) : Ctrl + F11
//		실행 방법(MacOS) : Command + Shift + F11
		
		
	}

}
